definition.script:
pipeline {
    agent any

    parameters {
        string(name: 'DOCKER_TAG', defaultValue: 'v1.0', description: 'Docker image tag')
    }

    environment {
        IMAGE_NAME = 'charansait372/node-js'
        TAG        = "${params.DOCKER_TAG}"
    }

    tools {
        nodejs 'node-20'
    }

    stages {
        stage('Git Checkout') {
            steps {
                git branch: 'master',
                    url: 'https://github.com/CharanSai8127/Node.js-app.git'
            }
        }

        stage('Setup NodeJS 20') {
            steps {
                sh 'node -v'
                sh 'npm -v'
            }
        }

        stage('Install Dependencies (Backend)') {
            steps {
                dir('server') {
                    sh 'npm install'
                }
            }
        }

        stage('Build Backend') {
            steps {
                dir('server') {
                    sh 'npm run build || echo "No build script, skipping"'
                }
            }
        }

        stage('OWASP Dependency Check') {
            steps {
                dir('server') {
                    dependencyCheck additionalArguments: '--scan . --format HTML --format XML --out .',
                                   odcInstallation: 'owasp'
                    dependencyCheckPublisher pattern: '**/dependency-check-report.html'
                }
            }
        }

        stage('SonarQube Analysis') {
            steps {
                // Install SonarScanner globally first
                sh 'npm install -g sonarqube-scanner'

                withSonarQubeEnv('sonar') {
                    sh '''
                        sonar-scanner \
                          -Dsonar.projectKey=node-js-app \
                          -Dsonar.projectName="Node.js App" \
                          -Dsonar.sources=server \
                          -Dsonar.exclusions=node_modules/**,dist/**,build/**,coverage/**
                    '''
                }
            }
        }

        stage('Quality Gate') {
            steps {
                timeout(time: 10, unit: 'MINUTES') {
                    waitForQualityGate abortPipeline: true
                }
            }
        }

        stage('Docker Build') {
            steps {
                sh 'docker build -t ${IMAGE_NAME}:${TAG} .'
            }
        }

        stage('Docker Push') {
            steps {
                withDockerRegistry(
                    credentialsId: 'docker-cred',
                    url: 'https://index.docker.io/v1/'
                ) {
                    sh 'docker push ${IMAGE_NAME}:${TAG}'
                }
            }
        }

        stage('Trivy Image Scan') {
            steps {
                script {
                    sh '''
                        trivy image \
                          --scanners vuln \
                          --format json \
                          -o trivy-report.json \
                          ${IMAGE_NAME}:${TAG}
                    '''
                    sh 'ls -la trivy-report.json || echo "File not created"'
                    sh '''
                        trivy image \
                          --exit-code 1 \
                          --no-progress \
                          --severity CRITICAL,HIGH \
                          ${IMAGE_NAME}:${TAG} || true
                    '''
                }
            }
        }
    }

    post {
        success {
            archiveArtifacts artifacts: 'trivy-report.json', 
                            fingerprint: true, 
                            allowEmptyArchive: true

            archiveArtifacts artifacts: '**/dependency-check-report.*', 
                            fingerprint: true, 
                            allowEmptyArchive: true

            archiveArtifacts artifacts: 'server/package-lock.json', 
                            fingerprint: true, 
                            allowEmptyArchive: true
        }
        failure {
            echo 'Pipeline failed. Archiving reports for debugging.'
            archiveArtifacts artifacts: 'trivy-report.json,**/dependency-check-report.*', 
                            allowEmptyArchive: true
        }
        always {
            cleanWs notFailBuild: true
        }
    }
}
