definition.script:
pipeline {
    agent any

    environment {
        IMAGE_TAG       = 'v1.2'
        DOCKER_HUB_USER = 'charansait372'
        REPORTS_DIR     = "${WORKSPACE}/security-reports"
    }

    stages {

        stage('Init') {
            steps {
                script {
                    env.TIMESTAMP = new Date().format('yyyyMMdd-HHmmss')
                }
            }
        }

        stage('Git Checkout') {
            steps {
                git branch: 'master',
                    url: 'https://github.com/CharanSai8127/full-stack-fastapi-template.git'
            }
        }

        stage('Prepare Workspace') {
            steps {
                sh '''
                  mkdir -p ${REPORTS_DIR}/owasp ${REPORTS_DIR}/trivy
                  rm -rf /var/lib/jenkins/.cache/trivy || true
                '''
            }
        }

        stage('OWASP Dependency Check') {
            steps {
                sh '''
                  set +e

                  pipx install safety || pip install --user safety
                  safety check --full-report \
                    --output ${REPORTS_DIR}/owasp/safety-${TIMESTAMP}.json || true

                  cd frontend
                  npm audit --json > ../${REPORTS_DIR}/owasp/npm-audit-${TIMESTAMP}.json || true
                  cd ..

                  set -e
                '''
            }
        }

        stage('Build Backend Image') {
            steps {
                sh '''
                  docker pull ${DOCKER_HUB_USER}/backend:${IMAGE_TAG} || true
                  docker build --pull \
                    --cache-from ${DOCKER_HUB_USER}/backend:${IMAGE_TAG} \
                    -t ${DOCKER_HUB_USER}/backend:${IMAGE_TAG} ./backend
                '''
            }
        }

        stage('Build Frontend Image') {
            steps {
                sh '''
                  docker pull ${DOCKER_HUB_USER}/frontend:${IMAGE_TAG} || true
                  docker build --pull \
                    --cache-from ${DOCKER_HUB_USER}/frontend:${IMAGE_TAG} \
                    -t ${DOCKER_HUB_USER}/frontend:${IMAGE_TAG} ./frontend
                '''
            }
        }

        stage('Trivy Image Scan (Safe Mode)') {
            steps {
                sh '''
                  trivy image \
                    --scanners vuln \
                    --skip-db-update \
                    --no-progress \
                    --format json \
                    --output ${REPORTS_DIR}/trivy/backend-${TIMESTAMP}.json \
                    ${DOCKER_HUB_USER}/backend:${IMAGE_TAG} || true

                  trivy image \
                    --scanners vuln \
                    --skip-db-update \
                    --no-progress \
                    --format json \
                    --output ${REPORTS_DIR}/trivy/frontend-${TIMESTAMP}.json \
                    ${DOCKER_HUB_USER}/frontend:${IMAGE_TAG} || true
                '''
            }
        }

        stage('Push Docker Images') {
            steps {
                withCredentials([
                    usernamePassword(
                        credentialsId: 'docker-cred',
                        usernameVariable: 'DOCKER_USER',
                        passwordVariable: 'DOCKER_PASS'
                    )
                ]) {
                    sh '''
                      echo "$DOCKER_PASS" | docker login \
                        -u "$DOCKER_USER" --password-stdin

                      docker push ${DOCKER_HUB_USER}/backend:${IMAGE_TAG}
                      docker push ${DOCKER_HUB_USER}/frontend:${IMAGE_TAG}

                      docker logout
                    '''
                }
            }
        }
    }

    post {
        success {
            archiveArtifacts artifacts: 'security-reports/**', fingerprint: true
            echo "Pipeline completed successfully"
        }

        failure {
            echo "Pipeline failed"
        }

        always {
            sh 'docker system prune -f || true'
        }
    }
}
